def solve1(s):
	x = list(
		_(s)
		.split("\n")
		.filter(lambda y : len(y) > 0)
		._
		)
	x = x[0]
	i = 0
	while i + 4 <= len(x):
		y = set(x[i:i+4])
		if len(y) == 4:
			print(i + 4)
			break
		i += 1

def solve2(s):
	x = list(
		_(s)
		.split("\n")
		.filter(lambda y : len(y) > 0)
		._
	)
	x = x[0]
	i = 0
	while i + 14 <= len(x):
		y = set(x[i:i+14])
		if len(y) == 14:
			print(i + 14)
			break
		i += 1

def solve(s):
	solve1(s)
	solve2(s)

print("sample:")
